<?phpheader('Content-Type:application/json;charset=UTF-8');$name = $_REQUEST['uname'];$upwd = $_REQUEST['upwd'];$conn = mysqli_connect('127.0.0.1','root','','mi',3306);$sql = 'SET NAMES UTF8';mysqli_query($conn,$sql);$sql = "SELECT * FROM mi_user WHERE uname='$name' AND upwd='$upwd'";$result = mysqli_query($conn,$sql);$output = ['code'=>0,'msg'=>null];if(!$result){    $output['code'] = 101;    $output['msg'] = 'sql语句错误!';}else{    $row = mysqli_fetch_assoc($result);    if($row===null){        $output['code'] = 102;        $output['msg'] = '用户名和密码错误!';    }else{        $output['code'] = 100;        $output['msg'] = '登录信息验证正确!';    }}echo json_encode($output);